import { useState, useEffect } from "react";
import { Row, Col, Container } from "react-bootstrap"
import {ArrowRightCircle} from "react-bootstrap-icons";
import headerImg from "../assets/img/header-img.svg";
export const Banner = () => {

    const [loopNum, setLoopNum] = useState(0);
    const [isDeleting, setIsDeleting] = useState(false);
    const toRotate = ["Web Developer", "Web Designer", "Designer"];
    const [text, setText] = useState('');
    const [delta, setDelta] = useState(300 - Math.random() * 100);
    const period = 2000;

    useEffect(() => {
        let ticker = setInterval(() => {
            tick();
        }, delta)

        return () => { clearInterval(ticker)};
    }, [text])


    const tick = () => {
        let i = loopNum % toRotate.length;
        let fullText = toRotate[i];
        let updatedText;
    
        if (isDeleting) {
            updatedText = fullText.substring(0, text.length - 1);
        } else {
            updatedText = fullText.substring(0, text.length + 1);
        }
    
        setText(updatedText);
    
        if (isDeleting && updatedText === '') {
            setIsDeleting(false);
            setLoopNum(loopNum + 1);
            setDelta(500);
        } else if (!isDeleting && updatedText === fullText) {
            setIsDeleting(true);
            setDelta(period);
        }
    }
    

    return (
        <section className="banner" id="home">
            <Container>
                <Row className = "align-item-center">
                    <Col xs = {12} md = {6}  xl = {7}>
                    <span className="tagline">Seja bem-vindo</span>
                    <h1>{`Oi, eu sou Desenvolvedor\n`} <span className="wrap">{text}</span></h1>
                    <p>TEXTO TEXTO TEXTO TEXTO TEXTO TEXTO</p>
                    <button onClick={() => console.log('connect')}>Vamos Conectar <ArrowRightCircle size={25}/></button>
                    </Col>
                    <Col xs = {12} md = {6}  xl = {5}>
                        <img src = {headerImg} alt="Header Img"/>
                    </Col>
                </Row>
            </Container>
        </section>
    )
}
